from flask import Flaskfrom utils import *app = Flask(__name__)if __name__ == '__main__':    @app.route("/")    def page_index():        candidates: list[dict] = get_all_candidates()        result = format_candidates(candidates)        return result    @app.route('/candidates/<int:pk>')    def get_candidate(pk):        candidate = get_candidate(pk)        result = format_candidates([candidate])        return resultapp.run()