import jsonfrom pprint import pprintcandidates = 'candidates.json'with open(candidates, 'r', encoding='utf-8') as f:    data = json.load(f)def load_candidates():    return datadef get_all():    arr = []    for item in data:        arr.append(item['skills'])        print(item['name'], item['position'], item['skills'])        return item['name'], item['position'], item['skills']def get_by_pk(pk):    for item in data:        if pk == item['pk']:            print(item['name'], item['position'])def get_by_skill(skill_name):    arr = []    for item in data:        if skill_name in item['skills']:            arr.append(item)            print(arr)get_all()